<html>  <head>    <link rel = "stylesheet" type = "text/css" href = "includes/styles/global.css">    <script type="text/x-mathjax-config">      MathJax.Hub.Config({        extensions: ["tex2jax.js"],        jax: ["input/TeX","output/HTML-CSS"],        tex2jax: {inlineMath: [["$","$"],["\\(","\\)"]]}      });    </script>    <script type="text/javascript" src="/MathJax/MathJax.js"></script>    <script type = "text/javascript">      function confirmDelete(text){        var r = confirm("Are you sure to remove the entry: " + text + ".");        if(r == true){          window.location = "gl-del-entry.php?entry=" + text;        }      }    </script>    <title>Glossary entries</title>  </head>  <body bgcolor = "#B8B894">    <table width = "990">      <tr>      </tr>      <tr>        <td width = "20%"></td>        <td><p class = "header" align = "center"><b>Glossary</b></p></td>        <td width = "20%"></td>      </tr>      <tr>        <td width = "20%"></td>        <td>          <br>          <?php            include_once "includes\php\markdown.php";            include_once "includes\php\smartypants.php";            # defining the database connection constants            #            define("DB_NAME", "db_name");            define("DB_USER", "db_user");            define("DB_PASS", "db_pass");            # prints a message at the top of the page            # whenever an entry is removed.            #            if(isset($_GET['removed'])){              if($_GET['removed'] == "true")                echo "<p align = \"center\">Entry <i>" . $_GET['entry'] . "</i> was successfully removed from glossary.<br></p>";              else                echo "<p align = \"center\">Could not remove entry <i>" . $_GET['entry'] . "</i>. Try later.</p>";;            }            # prints a message at the top of the page whenever a new entry            # is created or an existing entry is edited            #            if(isset($_GET['edited'])){              if($_GET['edited'] == "true")                echo "<p align = \"center\">Entry <i>" . $_GET['entry'] . "</i> was saved successfully.</p>";              else                echo "<p align = \"center\">Could not save entry <i>" . $_GET['entry'] . "</i>. Try later.</p>";            }            if(isset($_GET['start'])){              # prints all letters from A to Z              #              echo "<p class = \"content\" align = \"center\">";              for($letter = 0; $letter < 26; $letter++){                $ch = chr($letter + 65);                if($ch == $_GET['start']){                  echo "<b>" . $ch . "</b> ";                }                else{                  echo "<a href = \"gl-view-entries.php?start=" . $ch . "\" title = \"Entries starting with " . $ch . "\">". $ch ."</a> ";                }              }              echo "</p>";              # establishes database connection              #              $con = mysql_connect("localhost", DB_USER, DB_PASS);              if(!$con){                die('Could not connect to database: ' . mysql_error());              }              mysql_select_db(DB_NAME, $con);              $query = "SELECT * FROM glossary_entries";              $result = mysql_query($query) or die (mysql_error());              $letter = '\0';              # reads through all glossary entries fetched by $query from database              # and prints all entries begining with $_GET['start'];              while($row = mysql_fetch_array($result)){                if($row['title'][0] != $_GET['start']){                  continue;                }			                  if($row['title'][0] != $letter){                  $letter = $row['title'][0];                  echo "<p class = \"content-header\" align = \"center\"> <b>" . $letter. "</b></p>";                }                # prints current glossary entry                #                echo "<table width = \"100%\" bgcolor = \"#D6D6C2\" cellpadding = \"10\"><tr><td><p id = \"" . preg_replace('/\s+/i', '-', strtolower($row['title'])) . "\" class = \"content-header\"><b>" . $row['title'] . "</b> <a class = \"small\" title = \"Edit this entry\" href = \"gl-add-new-entry.php?edit=true&entry=" . $row['title'] . "\">edit</a> <a class = \"small\" title = \"Remove this entry\" href = \"#\" onclick = \"confirmDelete('" . $row['title']. "')\">remove</a> <br> <font face = \"sans-serif\" size = \"2pt\"><i>Last modified " . $row['last_modified'] . "</i></font><hr></p>" . MarkDown($row['content']) . "</td></tr></table> <br>";              }            }          ?>        </td>        <td width = "20%"></td>      </tr>      <tr>        <td colspan = "3">          <?php include 'gl-footer.php';?>        </td>      </tr>    </table>  </body></html>